export default function Hero (props) {
  return (
    <div className="relative p-28 bg-gradient-to-bl from-qpNavy to-qpDark text-center overflow-hidden">
      <div className="relative z-10 text-center w-2/3 mx-auto">
        <h1 className="text-5xl font-bold text bg-gradient-to-r from-white to-qpGreen-200">
          Right Commission. <span className="">Right Now.</span>
        </h1>
        <p className="p-6 text-white text-opacity-70 leading-loose"> 
          Give your reps transparency into their earnings, rewards the team you have, reaps the rewards of higher rep retention, increases in attainment, and record-breaking sales with automated commission tracking.
        </p>
        <div className="mt-6 flex items-center justify-center space-x-8">
          <button 
            onClick={() => props.onChangePage('planList')}
            className="px-6 py-2 rounded-md font-bold text-qpWater bg-white shadow-xl shadow-qpDark/5 hover:bg-qpSlate-700 hover:text-white">
              Discover Comp Plans &rarr;
          </button>
          <button 
            onClick={() => props.onChangePage('plan')}
            className="px-6 py-2 rounded-md bg-qpGreen-200 shadow-lg shadow-qpDark/10 text-white font-bold hover:bg-qpSlate-700">
              Design Your Plan &rarr;
          </button>
        </div>
      </div>

      {/* <img src="/assets/blob_left.svg" className="absolute -left-14 -top-40 w-1/4" />
      <img src="/assets/blob_right.svg" className="absolute -right-8 -top-2 w-1/4" /> */}
    </div>
  )
}
